import './App.css';
import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [total, setTotal] = useState(0.00)

  useEffect(() => {
    // Simular carregamento inicial do carrinho
    axios.get("http://localhost:4000/cart").then((response) => {
      setCart(response.data);
    });
  }, []);

  const fetchCart = async () => {
    try {
      const response = await axios.get("http://localhost:4000/cart");
      const cart = response.data;

      // Calcula o total diretamente após buscar o carrinho
      const result = cart.reduce((total, item) => total + item.price * item.quantity, 0);
      setTotal(result.toFixed(2));

      return cart;
    } catch (error) {
      console.error("Erro ao carregar o carrinho:", error);
      return [];
    }
  };

  useEffect(() => {
    const result = cart.reduce((total, item) => total + item.price * item.quantity, 0);
    setTotal(result.toFixed(2));
  }, [cart]);

  const handleAddItem = async (item) => {
    try {
      await axios.post("http://localhost:4000/cart/add", item);
      await fetchCart(); // Recarrega o carrinho após adicionar o item
    } catch (error) {
      console.error("Erro ao adicionar item:", error);
    }
  };

  const handleRemoveItem = async (itemName) => {
    try {
      await axios.post("http://localhost:4000/cart/remove", { name: itemName });
      await fetchCart(); // Recarrega o carrinho após remover o item
    } catch (error) {
      console.error("Erro ao remover item:", error);
    }
  };

  return (
    <>
    <div className="App">
        <h1>Items</h1>
        <div className="items">
          <div className="item">
            <h2>Banana</h2>
            <p>Price: R$9.99</p>
            <p>Quantity: 1</p>
            <button onClick={() => handleAddItem({
              name: "Banana",
              price: 9.99,
              quantity: 1,
            })}>Add to Cart</button>
          </div>
        </div>
    </div>
        <div className="App">
        <h1>Shopee Cart</h1>
        <ul>
        {cart.map((item, index) => (
          <li key={index}>
            {item.name} - R${item.price} x {item.quantity} = R${item.subtotal}
            <button onClick={() => handleRemoveItem(item.name)}>Remover</button>
          </li>
        ))}
        </ul>
        <h1>Total: R${total}</h1>
    </div>
    </>
  );
}

export default App;
