import './App.css';
import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [cart, setCart] = useState([]);
  useEffect(() => {
    // Simular carregamento inicial do carrinho
    axios.get("http://localhost:3000/cart").then((response) => {
      setCart(response.data);
    });
  }, []);

  const handleRemoveItem = (itemName) => {
    axios.post("http://localhost:3000/cart/remove", { name: itemName }).then(() => {
      setCart(cart.filter((item) => item.name !== itemName));
    });
  };
  return (
    <div className="App">
        <h1>Shopee Cart</h1>
        <div className="items">
          <div className="item">
            <h2>Item 1</h2>
            <p>Price: R$10.00</p>
            <p>Quantity: 1</p>
            <button>Add to Cart</button>
          </div>
        </div>
    </div>
  );
}

export default App;
